import React, { useEffect, useState } from 'react';
// hooks
import { validate } from '../../hooks';
import { useForm } from '../../hooks';
import { useAuth0 } from '@auth0/auth0-react';
// components
import { GovResourceForm } from '../../components';
import { PreviousExpForm } from '../../components';
import { EducationForm } from '../../components';
import { SocialNetworkForm } from '../../components';
import { EditButton } from '../../components';

// styles
import './Profile.css';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

// helpers
import { getTime } from '../../util/getTime';

function Profile({ user, createProfile }) {
  const [showGovResForm, setShowGovResForm] = useState(false);
  const [showEduForm, setShowEduForm] = useState(false);
  const [showSocialForm, setShowSocialForm] = useState(false);
  const [showPrevExpForm, setShowPrevExpForm] = useState(false);

  const login = (values) => {
    console.log(values);
  };

  const { errors, values, handleChange, handleSubmit } = useForm(
    login,
    validate
  );

  const greetWithTime = getTime();

  return (
    <div className="profile-root">
      <div className="header">
        <div>
          <img
            className="header-pic"
            src={user.picture}
            alt="profile-picture"
          />
        </div>
        <div className="header-greeting__container">
          <p className="header-greeting">
            {greetWithTime}, {user.given_name}!
          </p>
        </div>
      </div>
      <div className="forms-container">
        {!showGovResForm ? (
          <div>
            <div>
              <h2>Show Gov Resource Form</h2>
              <p>Display user info</p>
            </div>
            <div className="access-form__button">
              <EditButton onClick={setShowGovResForm((prev) => !prev)} />
            </div>
          </div>
        ) : (
          <div className="form">
            <div>
              <GovResourceForm
                values={values}
                handleChange={handleChange}
                errors={errors}
              />
            </div>
            <div className="access-form__button">
              <button onClick={setShowGovResForm((prev) => !prev)}>
                <KeyboardArrowDownIcon />
              </button>
            </div>
          </div>
        )}
        {!showEduForm ? (
          <div>
            <div>
              <h2>Show Education Form</h2>
              <p>Display user info</p>
            </div>
            <div className="access-form__button">
              <EditButton onClick={setShowEduForm((prev) => !prev)} />
            </div>
          </div>
        ) : (
          <div className="form">
            <div>
              <EducationForm
                values={values}
                handleChange={handleChange}
                errors={errors}
              />
            </div>
            <div className="access-form__button">
              <button onClick={setShowEduForm((prev) => !prev)}>
                <KeyboardArrowDownIcon />
              </button>
            </div>
          </div>
        )}
        {!showSocialForm ? (
          <div>
            <div>
              <h2>Show Social Network Form</h2>
              <p>Display user info</p>
            </div>
            <div className="access-form__button">
              <EditButton onClick={setShowSocialForm((prev) => !prev)} />
            </div>
          </div>
        ) : (
          <div>
            <div className="form">
              <SocialNetworkForm
                values={values}
                handleChange={handleChange}
                errors={errors}
              />
            </div>
            <div className="access-form__button">
              <button onClick={setShowSocialForm((prev) => !prev)}>
                <KeyboardArrowDownIcon />
              </button>
            </div>
          </div>
        )}
        {!forms.showPrevExpForm ? (
          <div>
            <div>
              <h2>Show Prev Exp Form</h2>
              <p>Display user info</p>
            </div>
            <div className="access-form__button">
              <EditButton onClick={setShowPrevExpForm((prev) => !prev)} />
            </div>
          </div>
        ) : (
          <div>
            <div className="form">
              <PreviousExpForm
                values={values}
                handleChange={handleChange}
                errors={errors}
              />
            </div>
            <div className="access-form__button">
              <button onClick={setShowPrevExpForm((prev) => !prev)}>
                <KeyboardArrowDownIcon />
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
