import React, { useEffect, useState } from 'react';
// hooks
import { validate } from '../../hooks';
import { useForm } from '../../hooks';
import { useAuth0 } from '@auth0/auth0-react';
// components
import { GovResourceForm } from '../../components';
import { PreviousExpForm } from '../../components';
import { EducationForm } from '../../components';
import { SocialNetworkForm } from '../../components';
// styles
import './Profile.css';

function Profile({ user, createProfile }) {
  const getTime = () => {
    var d = new Date();
    var time = d.getHours();

    if (time < 12) {
      return 'Good morning';
    }
    if (time > 12 && time <= 17) {
      return 'Good afternoon';
    }
    if (time > 17 && time < 24) {
      return 'Good evening';
    }
  };

  const { errors, values, handleChange, handleSubmit } = useForm(login, validate);

  return (
    <div className="profile-root">
      <div className="header">
        <div>
          <img
            className="header-pic"
            src={user.picture}
            alt="profile-picture"
          />
        </div>
        <div className="header-greeting__container">
          <p className="header-greeting">
            {getTime()}, {user.given_name}!
          </p>
        </div>
      </div>
      <div className="profile-form">
        <form onSubmit={handleSubmit}>
          <GovResourceForm
            values={values}
            handleChange={handleChange}
            errors={errors}
          />
          <EducationForm values={values} handleChange={handleChange} />
          <SocialNetworkForm values={values} handleChange={handleChange} />
          <PreviousExpForm values={values} handleChange={handleChange} />
          <div className="submit-buttons">
            <button>Save</button>
            <input type="reset" value="Clear" />
          </div>
        </form>
      </div>
    </div>
  );
}

export default Profile;
