import React, { useEffect, useState } from 'react';
// hooks
import { validate } from '../../hooks';
import { useForm } from '../../hooks';
import { useAuth0 } from '@auth0/auth0-react';
// components
import { GovResourceForm } from '../../components';
import { PreviousExpForm } from '../../components';
import { EducationForm } from '../../components';
import { SocialNetworkForm } from '../../components';
import { EditButton } from '../../components';

// styles
import './Profile.css';

function Profile({ user, createProfile }) {
  const { showForm, setShowForm } = useState(false);

  const getTime = () => {
    var d = new Date();
    var time = d.getHours();

    if (time < 12) {
      return 'Good morning';
    }
    if (time > 12 && time <= 17) {
      return 'Good afternoon';
    }
    if (time > 17 && time < 24) {
      return 'Good evening';
    }
  };

  const login = (values) => {
    console.log(values);
  };

  const { errors, values, handleChange, handleSubmit } = useForm(
    login,
    validate
  );

  return (
    <div className="profile-root">
      <div className="header">
        <div>
          <img
            className="header-pic"
            src={user.picture}
            alt="profile-picture"
          />
        </div>
        <div className="header-greeting__container">
          <p className="header-greeting">
            {getTime()}, {user.given_name}!
          </p>
        </div>
      </div>
      <div className="forms-container">
        {!showForm ? (
          <>
            <div>
              <h2>Show Form</h2>
              <p>Display user info</p>
            </div>
            <div>
              <EditButton showForm={showForm} setShowForm={setShowForm} />
            </div>
          </>
        ) : (
          <div className="form">
            <GovResourceForm
              values={values}
              handleChange={handleChange}
              errors={errors}
            />
          </div>
        )}
        <div className="form">
          <EducationForm
            values={values}
            handleChange={handleChange}
            errors={errors}
          />
        </div>
        <div className="form">
          <SocialNetworkForm
            values={values}
            handleChange={handleChange}
            errors={errors}
          />
        </div>
        <div className="form">
          <PreviousExpForm
            values={values}
            handleChange={handleChange}
            errors={errors}
          />
        </div>
      </div>
    </div>
  );
}

export default Profile;
