export default function validate(values) {
  // const errors = {};
  // switch (values) {
  //   case !values.email:
  //     errors.email = 'Email address is required';
  //     return errors;
  //   case !/\S+@\S+\.\S+/.test(values.email):
  //     errors.email = 'Email address is invalid';
  //     return errors;
  //   default:
  //     return errors;
  // }
  const validateEmail = () => {
    const errors = {};
    if (!values.email) {
      errors.email = 'Email address is required';
    } else if (!/\S+@\S+\.\S+/.test(values.email)) {
      errors.email = 'Email address is invalid';
    }
    return errors;
  };
  return {
    validateEmail
  };
}
