import React from 'react';
import { LandingPage } from '../LandingPage';
import Profile from '../Profile/Profile';
import { Network } from '../Network';
import { Jobs } from '../Jobs';
import { Nav } from '../../components';
import { Settings } from '../../components';
import './App.css';

import { withAuthenticationRequired, useAuth0 } from '@auth0/auth0-react';

import { Route, Switch } from 'react-router-dom';

const App = () => {
  const { isLoading, isAuthenticated, user } = useAuth0();

  const ProtectedRoute = ({ component, ...args }) => (
    <Route component={withAuthenticationRequired(component)} {...args} />
  );

  const createProfile = (profileData) => {
    fetch(`http://localhost:5000/api/profile`, {
      headers: {
        'Content-Type': 'application/json'
      },
      method: 'PUT',
      body: JSON.stringify({
        firstName: profileData.firstName,
        lastName: profileData.lastName,
        email: profileData.email,
        gender: profileData.gender,
        bio: profileData.bio,
        jobTitle: profileData.jobTitle,
        industry: profileData.industry,
        // start experience object
        previousTitle: profileData.previousTitle,
        company: profileData.company,
        location: profileData.location,
        previousFromDate: profileData.previousFromDate,
        previousToDate: profileData.previousToDate,
        currentJob: profileData.currentJob,
        previousJobDescription: profileData.previousJobDescription,
        // start education object
        school: profileData.school,
        degree: profileData.degree,
        fieldOfStudy: profileData.fieldOfStudy,
        schoolFrom: profileData.schoolFrom,
        schoolTo: profileData.schoolTo,
        // start social
        twitter: profileData.twitter,
        linkedin: profileData.linkedin,
        instagram: profileData.instagram
      })
    })
      .then((res) => {
        if (res.status === 422) {
          throw new Error(
            "Validation failed. Make sure the email address isn't used yet!"
          );
        }
        if (res.status !== 200 && res.status !== 201) {
          throw new Error('Creating a user failed!');
        }
        return res.json();
      })
      .then((resData) => {
        this.props.history.replace('/');
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // Create <Loading /> component i.e. spinner
  if (isLoading) {
    return 'Loading...';
  }

  return (
    <>
      <Nav user={user} isAuth={isAuthenticated} />
      <Switch>
        <Route exact path="/" render={(routeProps) => <LandingPage />} />
        <Route
          path="/profile/:id"
          render={(routeProps) => (
            <Profile createProfile={createProfile} user={user} />
          )}
        />
        <Route path="/network/:id" component={Network} />
        <Route path="/jobs/:id" component={Jobs} />
        <Route path="/settings" component={Settings} />
      </Switch>
    </>
  );
};

export default App;
