import React from 'react';
import { useForm, validate, useGetStates } from '../../../hooks';

const GovResourceForm = () => {
  const { errors, values, handleChange, handleSubmit } = useForm(
    login,
    validate
  );
  const { result } = useGetStates();
  function login(values) {
    console.log(values);
  }
  return (
    <form onSubmit={handleSubmit}>
      <h3>Gov Resource Network Profile</h3>
      <div className="input-field">
        <label htmlFor="firstName">First Name</label>
        <input
          type="text"
          name="firstName"
          id="firstName"
          value={values.firstName || ''}
          onChange={handleChange}
        />
      </div>
      <div className="input-field">
        <label htmlFor="lastName">Last Name</label>
        <input
          type="text"
          name="lastName"
          id="lastName"
          value={values.lastName || ''}
          onChange={handleChange}
        />
      </div>
      <div className="input-field">
        <select name="country" className="countries" id="countryId">
          <option value="">Select Country</option>
        </select>
        <select name="state" className="states" id="stateId">
          <option value="">Select State</option>
        </select>
        <select name="city" className="cities" id="cityId">
          <option value="">Select City</option>
        </select>
      </div>
      <div className="input-field">
        <label htmlFor="email">Email</label>
        <input
          className={`input ${errors.email && 'danger'}`}
          type="email"
          name="email"
          id="email"
          value={values.email || ''}
          onChange={(e) => handleChange(e)}
        />
        {errors.email && <p className="help">{errors.email}</p>}
      </div>
      <div className="input-field">
        <label htmlFor="jobTitle">Job Title</label>
        <input
          type="text"
          name="jobTitle"
          id="jobTitle"
          value={values.jobTitle || ''}
          onChange={handleChange}
        />
      </div>
      <div className="input-field">
        <label htmlFor="industry">Industry</label>
        <input
          type="text"
          name="industry"
          id="industry"
          value={values.industry || ''}
          onChange={handleChange}
        />
      </div>
      <div className="input-field">
        <label htmlFor="website">Website</label>
        <input
          type="text"
          name="website"
          id="website"
          value={values.website || ''}
          onChange={handleChange}
        />
      </div>
      <div className="input-field">
        <label htmlFor="bio">Bio</label>
        <textarea
          name="bio"
          id="bio"
          rows="4"
          value={values.bio || ''}
          onChange={handleChange}
        />
      </div>
      <div className="submit-buttons">
        <button>Save</button>
      </div>
    </form>
  );
};

export default GovResourceForm;
