import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth0 } from '@auth0/auth0-react';

const GovResourceCard = () => {
  const { user } = useAuth0();
  const [state, setState] = useState({});
  const [isMounted, setIsMounted] = useState(false);
  useEffect(() => {
    console.log('inside useEffect');
    axios
      .get(`http://localhost:5000/api/profile/${user.sub}`)
      .then((response) => {
        setState((prev) => ({
          ...prev,
          ...response.data.profile
        }));
      })
      .catch((err) => console.log(err.message));
    setIsMounted(true);
  }, [user.sub]);
  // const cardDisplay = () => {
  //   const { firstName, lastName } = isMounted && state.data.profile;
  //   return <h2>Hello</h2>;
  // };
  return (
    <div>
      {state.data.profile.firstName} {state.data.profile.lastName}
    </div>
  );
};

export default GovResourceCard;
