  const getTime = () => {
    var d = new Date();
    var time = d.getHours();

    if (time < 12) {
      return 'Good morning';
    }
    if (time > 12 && time < 17) {
      return 'Good afternoon';
    }
    if (time > 17 && time < 24) {
      return 'Good evening';
    }
  };

  return (
    <div className="profile-root">
      <div className="header">
        <div>
          <img
            className="header-pic"
            src={props.user.picture}
            alt="profile-picture"
          />
        </div>
        <div className="header-greeting__container">
          <p className="header-greeting">
            {getTime()}, {props.user.given_name}!
          </p>
        </div>
      </div>
      <ProfileForm />
    </div>
  );
}

export default Profile;
