import React from 'react';
import Wrapper from './Wrapper';
import { LandingPage } from '../LandingPage';
import { Profile } from '../Profile';
import { Network } from '../Network';
import { Jobs } from '../Jobs';
import { Nav } from '../../components';
import { Settings } from '../../components';
import './App.css';

import { withAuthenticationRequired, useAuth0 } from '@auth0/auth0-react';

import { Route, Switch, Router } from 'react-router-dom';
import { render } from 'react-dom';

import { createBrowserHistory } from 'history';

const App = () => {
  const { isLoading, error, isAuthenticated, user } = useAuth0();

  const history = createBrowserHistory();

  const ProtectedRoute = ({ component, ...args }) => (
    <Route component={withAuthenticationRequired(component)} {...args} />
  );

  signupHandler = (authData) => {
    fetch('http://localhost:5000/auth/signup', {
      headers: {
        'Content-Type': 'application/json'
      },
      method: 'PUT',
      body: JSON.stringify({
        email: authData.email,
        name: authData.name,
        password: authData.password && authData.password
      })
    })
      .then((res) => {
        if (res.status === 422) {
          throw new Error(
            "Validation failed. Make sure the email address isn't used yet!"
          );
        }
        if (res.status !== 200 && res.status !== 201) {
          throw new Error('Creating a user failed!');
        }
        return res.json();
      })
      .then((resData) => {
        console.log(resData);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <Wrapper>
      <Router history={history}>
        <Nav
          onSignup={signupHandler}
          id={user && user.sub}
          isAuth={isAuthenticated}
        />
        <Switch>
          <Route exact path="/" render={(routeProps) => <LandingPage />} />
          <Route
            path="/profile/:id"
            render={(routeProps) => <Profile user={user} />}
          />
          <Route path="/network/:id" component={Network} />
          <Route path="/jobs/:id" component={Jobs} />
          <Route path="/settings" component={Settings} />
        </Switch>
      </Router>
    </Wrapper>
  );
};

export default App;
